<?php

namespace WBS\Tests\Services\Facade\Products;

use GuzzleHttp\Client;
use Http\Message\MessageFactory\GuzzleMessageFactory;
use PHPUnit\Framework\TestCase;
use Transloyd\Services\ESign\{ESign, ESignManager, Provider};

class ESignManagerTest extends TestCase
{
    private $client;
    private $provider;

    protected function setUp(): void
    {
        parent::setUp();

        $this->client = new Client();
        $this->provider = new Provider(
            $this->client,
            new GuzzleMessageFactory()
        );
    }

    public function testCreateSession(): void
    {
        $documentInBase64 = "";
        $keyDataInBase64 = "";
        $eSign = new ESign($this->provider);
        $eSignManager = new ESignManager($eSign);
        $this->assertNotEmpty(
            $eSignManager->getSignedDocumentRaw($documentInBase64, $keyDataInBase64, 123)
        );
    }

    public function testCheckVerifyFile(): void
    {
        $base64File = '';

        $eSign = new ESign($this->provider);
        $eSignManager = new ESignManager($eSign);

        $this->assertNotEmpty(
            $eSignManager->checkVerify($base64File)
        );
    }
}